import React, { useCallback, useContext, useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import SummaryApi from '../common';
import { FaStar, FaStarHalf } from "react-icons/fa";
import displayINRCurrency from '../helpers/displayCurrency';
import CategoryWiseProductDisplay from '../components/CategoryWiseProductDisplay';
import addToCart from '../helpers/addToCart';
import Context from '../context';

const ProductDetails = () => {
  const [data, setData] = useState({
    productName: "",
    brandName: "",
    category: "",
    productImage: [],
    description: "",
    price: "",
    sellingPrice: ""
  });
  const params = useParams();
  const [loading, setLoading] = useState(true);
  const [activeImage, setActiveImage] = useState("");
  const [zoomImageCoordinate, setZoomImageCoordinate] = useState({ x: 0, y: 0 });
  const [zoomImage, setZoomImage] = useState(false);

  const { fetchUserAddToCart } = useContext(Context);
  const navigate = useNavigate();

  const fetchProductDetails = async () => {
    try {
      setLoading(true);
      const response = await fetch(SummaryApi.productDetails.url, {
        method: SummaryApi.productDetails.method,
        headers: { "content-type": "application/json" },
        body: JSON.stringify({ productId: params.id })
      });

      if (!response.ok) throw new Error('Network response was not ok.');

      const dataResponse = await response.json();
      setData(dataResponse?.data || {});
      setActiveImage(dataResponse?.data?.productImage[0] || "");
    } catch (error) {
      console.error("Failed to fetch product details:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProductDetails();
  }, [params.id, fetchProductDetails]);

  const handleMouseEnterProduct = (imageURL) => {
    setActiveImage(imageURL);
  };

  const handleZoomImage = useCallback((e) => {
    setZoomImage(true);
    const { left, top, width, height } = e.target.getBoundingClientRect();
    const x = (e.clientX - left) / width;
    const y = (e.clientY - top) / height;
    setZoomImageCoordinate({ x, y });
  }, []);

  const handleLeaveImageZoom = () => {
    setZoomImage(false);
  };

  const handleAddToCart = async (e, id) => {
    e.stopPropagation(); // Prevent event from bubbling up
    try {
      await addToCart(e, id);
      fetchUserAddToCart();
    } catch (error) {
      console.error("Failed to add to cart:", error);
    }
  };

  const handleBuyProduct = async (e, id) => {
    e.stopPropagation(); // Prevent event from bubbling up
    try {
      await addToCart(e, id);
      fetchUserAddToCart();
      navigate("/cart");
    } catch (error) {
      console.error("Failed to buy product:", error);
    }
  };

  return (
    <div className='container mx-auto p-4'>
      <div className='min-h-[200px] flex flex-col lg:flex-row gap-4'>
        {/* Product Image */}
        <div className='h-96 flex flex-col lg:flex-row-reverse gap-4'>
          <div className='h-[300px] w-[300px] lg:h-96 lg:w-96 bg-slate-200 relative p-2'>
            <img
              src={activeImage}
              className='h-full w-full object-cover mix-blend-multiply'
              alt='Product'
              onMouseMove={handleZoomImage}
              onMouseLeave={handleLeaveImageZoom}
            />
            {zoomImage && (
              <div className='hidden lg:block absolute min-w-[500px] min-h-[400px] bg-slate-200 p-1 -right-[510px] top-0'>
                <div
                  className='w-full h-full min-h-[400px] min-w-[500px] mix-blend-multiply scale-150'
                  style={{
                    background: `url(${activeImage})`,
                    backgroundRepeat: 'no-repeat',
                    backgroundPosition: `${zoomImageCoordinate.x * 100}% ${zoomImageCoordinate.y * 100}%`
                  }}
                />
              </div>
            )}
          </div>
          <div className='h-full flex gap-2 lg:flex-col overflow-scroll scrollbar-none'>
            {loading ? (
              new Array(4).fill(null).map((_, index) => (
                <div className='h-20 w-20 bg-slate-200 rounded animate-pulse' key={`loadingImage${index}`} />
              ))
            ) : (
              data.productImage.map((imgURL) => (
                <div className='h-20 w-20 bg-slate-200 rounded p-1' key={imgURL}>
                  <img
                    src={imgURL}
                    alt='Product Thumbnail'
                    className='w-full h-full object-cover mix-blend-multiply cursor-pointer'
                    onMouseEnter={() => handleMouseEnterProduct(imgURL)}
                    onClick={() => handleMouseEnterProduct(imgURL)}
                  />
                </div>
              ))
            )}
          </div>
        </div>

        {/* Product Details */}
        <div className='flex flex-col gap-1'>
          {loading ? (
            <div className='grid gap-1 w-full'>
              <p className='bg-slate-200 animate-pulse h-6 lg:h-8 w-full rounded-full' />
              <h2 className='text-2xl lg:text-4xl font-medium bg-slate-200 animate-pulse h-6 lg:h-8 w-full'></h2>
              <p className='capitalize text-slate-400 bg-slate-200 h-6 lg:h-8 w-full' />
              <div className='text-red-600 bg-slate-200 h-6 lg:h-8 animate-pulse flex items-center gap-1 w-full' />
              <div className='flex items-center gap-2 text-2xl lg:text-3xl font-medium my-1 h-6 lg:h-8 animate-pulse w-full'>
                <p className='text-red-600 bg-slate-200 w-full' />
                <p className='text-slate-400 line-through bg-slate-200 w-full' />
              </div>
              <div className='flex items-center gap-3 my-2 w-full'>
                <button className='h-6 lg:h-8 bg-slate-200 rounded animate-pulse w-full' />
                <button className='h-6 lg:h-8 bg-slate-200 rounded animate-pulse w-full' />
              </div>
              <div className='w-full'>
                <p className='text-slate-600 font-medium my-1 h-6 lg:h-8 bg-slate-200 rounded animate-pulse w-full' />
                <p className='bg-slate-200 rounded animate-pulse h-10 lg:h-12 w-full' />
              </div>
            </div>
          ) : (
            <>
              <p className='bg-red-200 text-red-600 px-2 rounded-full inline-block w-fit'>{data.brandName}</p>
              <h2 className='text-2xl lg:text-4xl font-medium'>{data.productName}</h2>
              <p className='capitalize text-slate-400'>{data.category}</p>
              <div className='text-red-600 flex items-center gap-1'>
                <FaStar />
                <FaStar />
                <FaStar />
                <FaStar />
                <FaStarHalf />
              </div>
              <div className='flex items-center gap-2 text-2xl lg:text-3xl font-medium my-1'>
                <p className='text-red-600'>{displayINRCurrency(data.sellingPrice)}</p>
                <p className='text-slate-400 line-through'>{displayINRCurrency(data.price)}</p>
              </div>
              <div className='flex items-center gap-3 my-2'>
                <button
                  className='border-2 border-red-600 rounded px-3 py-1 min-w-[120px] text-red-600 font-medium hover:bg-red-600 hover:text-white'
                  onClick={(e) => handleBuyProduct(e, data._id)}
                >
                  Buy
                </button>
                <button
                  className='border-2 border-red-600 rounded px-3 py-1 min-w-[120px] font-medium text-white bg-red-600 hover:text-red-600 hover:bg-white'
                  onClick={(e) => handleAddToCart(e, data._id)}
                >
                  Add To Cart
                </button>
              </div>
              <div>
                <p className='text-slate-600 font-medium my-1'>Description:</p>
                <p>{data.description}</p>
              </div>
            </>
          )}
        </div>
      </div>

      {data.category && (
        <CategoryWiseProductDisplay category={data.category} heading="Recommended Products" />
      )}
    </div>
  );
};

export default ProductDetails;
