import React, { useContext, useEffect, useState } from 'react';
import Context from '../context';
import { MdDelete } from "react-icons/md";
import SummeryApi from '../common';
import displayPKRCurrency from '../helpers/displayCurrency';

const Cart = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const context = useContext(Context);

    const fetchData = async () => {
        try {
            const response = await fetch(SummeryApi.addToCartProductView.url, {
                method: SummeryApi.addToCartProductView.method,
                credentials: 'include',
                headers: {
                    "Content-Type": 'application/json'
                },
            });

            // Check if the response is OK
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            // Try parsing JSON
            const responseData = await response.json();

            // Validate response data
            if (responseData.success) {
                setData(responseData.data);
            } else {
                console.error("Failed to fetch cart data:", responseData.message);
                setData([]); // Ensure data is cleared on failure
            }
        } catch (error) {
            console.error("Error fetching cart data:", error);
            setData([]); // Ensure data is cleared on failure
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const updateQuantity = async (id, qty, increment = true) => {
        try {
            const newQty = increment ? qty + 1 : Math.max(qty - 1, 1);
            const response = await fetch(SummeryApi.updateCartProduct.url, {
                method: SummeryApi.updateCartProduct.method,
                credentials: 'include',
                headers: {
                    "Content-Type": 'application/json'
                },
                body: JSON.stringify({ _id: id, quantity: newQty })
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const responseData = await response.json();
            if (responseData.success) {
                fetchData();
            } else {
                console.error("Failed to update quantity:", responseData.message);
            }
        } catch (error) {
            console.error("Error updating quantity:", error);
        }
    };

    const deleteCartProduct = async (id) => {
        try {
            const response = await fetch(deleteCartProduct.url, {
                method: SummeryApi.deleteCartProduct.method,
                credentials: 'include',
                headers: {
                    "Content-Type": 'application/json'
                },
                body: JSON.stringify({ _id: id })
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const responseData = await response.json();
            if (responseData.success) {
                fetchData();
                context.fetchUserAddToCart();
            } else {
                console.error("Failed to delete product:", responseData.message);
            }
        } catch (error) {
            console.error("Error deleting product:", error);
        }
    };

    const totalQty = data.reduce((total, item) => total + item.quantity, 0);
    const totalPrice = data.reduce((total, item) => total + (item.quantity * item.productId.sellingPrice), 0);

    return (
        <div className='container mx-auto'>
            <div className='text-center text-lg my-3'>
                {!loading && data.length === 0 && <p className='bg-white py-5'>No Data</p>}
            </div>

            <div className='flex flex-col lg:flex-row gap-10 lg:justify-between p-4'>
                <div className='w-full max-w-3xl'>
                    {loading ? (
                        new Array(4).fill(null).map((_, index) => (
                            <div key={`loading-${index}`} className='w-full bg-slate-200 h-32 my-2 border border-slate-300 animate-pulse rounded'></div>
                        ))
                    ) : (
                        data.map(product => (
                            <div key={product._id} className='w-full bg-white h-32 my-2 border border-slate-300 rounded grid grid-cols-[128px,1fr]'>
                                <div className='w-32 h-32 bg-slate-200'>
                                    <img src={product.productId.productImage[0]} className='w-full h-full object-cover' alt={product.productId.productName} />
                                </div>
                                <div className='px-4 py-2 relative'>
                                    <div className='absolute right-0 text-red-600 rounded-full p-2 hover:bg-red-600 hover:text-white cursor-pointer' onClick={() => deleteCartProduct(product._id)}>
                                        <MdDelete />
                                    </div>
                                    <h2 className='text-lg lg:text-xl text-ellipsis line-clamp-1'>{product.productId.productName}</h2>
                                    <p className='capitalize text-slate-500'>{product.productId.category}</p>
                                    <div className='flex items-center justify-between'>
                                        <p className='text-red-600 font-medium text-lg'>{displayPKRCurrency(product.productId.sellingPrice)}</p>
                                        <p className='text-slate-600 font-semibold text-lg'>{displayPKRCurrency(product.productId.sellingPrice * product.quantity)}</p>
                                    </div>
                                    <div className='flex items-center gap-3 mt-1'>
                                        <button className='border border-red-600 text-red-600 hover:bg-red-600 hover:text-white w-6 h-6 flex justify-center items-center rounded' onClick={() => updateQuantity(product._id, product.quantity, false)}>-</button>
                                        <span>{product.quantity}</span>
                                        <button className='border border-red-600 text-red-600 hover:bg-red-600 hover:text-white w-6 h-6 flex justify-center items-center rounded' onClick={() => updateQuantity(product._id, product.quantity, true)}>+</button>
                                    </div>
                                </div>
                            </div>
                        ))
                    )}
                </div>

                <div className='mt-5 lg:mt-0 w-full max-w-sm'>
                    {loading ? (
                        <div className='h-36 bg-slate-200 border border-slate-300 animate-pulse'></div>
                    ) : (
                        <div className='h-36 bg-white'>
                            <h2 className='text-white bg-red-600 px-4 py-1'>Summary</h2>
                            <div className='flex items-center justify-between px-4 gap-2 font-medium text-lg text-slate-600'>
                                <p>Quantity</p>
                                <p>{totalQty}</p>
                            </div>
                            <div className='flex items-center justify-between px-4 gap-2 font-medium text-lg text-slate-600'>
                                <p>Total Price</p>
                                <p>{displayPKRCurrency(totalPrice)}</p>
                            </div>
                            <button className='bg-blue-600 p-2 text-white w-full mt-2'>Payment</button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Cart;
