import React, { useState } from "react";
import ROLE from "../common/role";
import { IoCloseSharp } from "react-icons/io5";
import SummeryApi from "../common";

const ChsngeUserRole = ({ name, email, role, onClose }) => {
  const [userRole, setUserRole] = useState(role);

  const handleOnChangeSelect = (e) => {
    setUserRole(e.target.value);
  };

const updateUserRole = async() => {
 const fetchResponse = await fetch(SummeryApi.updateUser.url,{
      method: SummeryApi.updateUser.method,
      credentials: "include",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify({
        role : userRole
      })
 });
 const responseData = await fetchResponse.json();
 console.log("role update",responseData);

}

  return (
    <div className="fixed top-0 bottom-0 left-0 right-0 w-full h-full z-10 flex justify-between items-center bg-slate-200 bg-opacity-0.">
      <div className=" mx-auto bg-white shadow-md p-4 w-full max-w-sm ">
        <button className="block ml-auto" onClick={onClose}>
          <IoCloseSharp />
        </button>

        <h1 className="pb-4 text-lg font-medium">Chsnge User Role</h1>
        <p>Name : {name}</p>
        <p>Email : {email}</p>

        <div className="flex items-center justify-between my-4">
          <p>Role :</p>
          <select
            className="border py-2 px-1"
            value={userRole}
            onChange={handleOnChangeSelect}
          >
            {Object.values(ROLE).map((el) => {
              return (
                <option value={el} key={el}>
                  {el}
                </option>
              );
            })}
          </select>
        </div>
        <button className="w-fit mx-auto block py-1 px-3 rounded-full bg-red-600 text-white hover:bg-red-700" onClick={updateUserRole}>
          Change Role
        </button>
      </div>
    </div>
  );
};

export default ChsngeUserRole;
