import React, { useEffect, useRef, useState } from 'react';
import fetchCategoryWiseProduct from '../helpers/fetchCategoryWiseProduct';
import displayPKRCurrency from "../helpers/displayCurrency";
import { FaAngleLeft, FaAngleRight } from "react-icons/fa6";

const HorizentalCardProduct = ({category,heading}) => {
    const [data,setData] = useState([]);
    const [loading,setLoading] = useState(false);
    const loadingList = new Array(13).fill(null);

    const [scroll,setScroll] = useState(0);
    const scrollElement = useRef();

const fetchData = async() => {
    setLoading(true);
    const categoryProduct = await fetchCategoryWiseProduct(category);
    setLoading(false);
    setData(categoryProduct?.data)
}


useEffect(()=>{
    fetchData();
},[])

const scrollRight = () => {
    scrollElement.current.scrollLeft += 300
}
const scrollLeft = () => {
    scrollElement.current.scrollLeft -= 300
}


  return (
    <div className='container mx-auto px-4 my-6 relative'>
        <h2 className='text-2xl py-4 font-semibold'>{heading}</h2>
        <div className='flex items-center gap-4 md:gap-6 overflow-scroll scrollbar-none transition-all' ref={scrollElement}>
        <button
           
              className="bg-white shadow-md rounded-full p-1 absolute left-0 text-lg hidden md:block"
            >
              <FaAngleLeft />
            </button>
            <button
             onClick={scrollRight}
              className="bg-white shadow-md rounded-full p-1 absolute right-0 text-lg hidden md:block"
            >
              <FaAngleRight />
            </button>
        {data.map((product,index)=>{
            return(
                <div className='w-full min-w-[280px] md:min-w-[320px] max-w-[280px] md:max-w-[320px] h-36 bg-white rounded-sm shadow flex'>
                <div className='bg-slate-200 h-full p-4  min-w-[120px] md:min-w-[145px]'>
                    <img src={product.productImage[0]}  alt='' className='h-full object-scale-down hover:scale-110 transition-all'/>
                </div>
                <div className='p-4'>
                  <h2 className='font-medium text-base md:text-lg text-ellipsis line-clamp-1 text-black'>{product?.productName}</h2> 
                  <p className='capitalize text-slate-500'>{product?.category}</p>
                  <div className=''>
                  <p className='text-red-600 font-medium'>{displayPKRCurrency(product?.sellingPrice)}</p>
                  <p className='text-slate-500 line-through'>{displayPKRCurrency(product?.price)}</p>
                  </div>
                  <button className=' bg-red-600 hover:bg-red-700 text-white px-3 py-0 rounded-full'>Add to Cart</button>
                </div>
              </div>
            )
        })}
        </div>
 </div>
  )
}

export default HorizentalCardProduct;
