import React, { useEffect, useState } from 'react';
import SummeryApi from '../common';
import { toast } from 'react-toastify';
import moment from "moment";
import { MdModeEditOutline } from "react-icons/md";


const AllUsers = () => {
    const [allUsers, setAllUsers] = useState([]);

    const fetchAllUsers = async () => {
        try {
            const fetchData = await fetch(SummeryApi.allUsers.url, {
                method: SummeryApi.allUsers.method,
                credentials: "include",
            });
            const dataResponse = await fetchData.json();
            
            if (dataResponse.success) {
                setAllUsers(dataResponse.data);
            }
            if (dataResponse.error) {
                toast.error(dataResponse.message);
            }
        } catch (error) {
            console.error("Error fetching all users:", error);
            toast.error("Failed to fetch users. Please try again later.");
        }
    };

    useEffect(() => {
        fetchAllUsers();
    }, []);

    return (
        <div>
            <table className='w-full userTable'>
                <thead>
                    <tr>
                        <th>Sr.No</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Created Date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {allUsers.map((el, index) => (
                        <tr key={el._id}>
                            <td>{index + 1}</td>
                            <td>{el?.name}</td>
                            <td>{el?.email}</td>
                            <td>{el?.role}</td>
                            <td>{moment(el?.createdAt).format("LL")}</td>
                            <td>
                                <button 
                                    className='bg-green-100 p-2 rounded-full cursor-pointer hover:bg-green-400 hover:text-white focus:outline-none'
                                    onClick={() => {} /* Add onClick logic here */}
                                >
                                    <MdModeEditOutline />
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AllUsers;
