import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import SummeryApi from "../common";
import { FaStar } from "react-icons/fa6";
import { FaStarHalf } from "react-icons/fa";
import displayPKRCurrency from "../helpers/displayCurrency";


const ProductDetails = () => {
  const [data, setData] = useState({
    productName: "",
    brandName: "",
    category: "",
    productImage: [],
    description: "",
    price: "",
    sellingPrice: "",
  });
  const params = useParams();
  console.log("product id", params);
  const [loading, setLoading] = useState(true);
  const productImageListLoading = new Array(4).fill(null);
  const [activeImage,setActiveImage] = useState("");
  const fetchProductDetails = async () => {
    setLoading(true);
    const response = await fetch(SummeryApi.productDetails.url, {
      method: SummeryApi.productDetails.method,
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify({
        productId: params?.id,
      }),
    });
     setLoading(false);
    const dataResponse = await response.json();
    setData(dataResponse?.data);
    setActiveImage(dataResponse?.data?.productImage[0])
  };
  useEffect(() => {
    fetchProductDetails();
  }, []);

   const handleMouseEnterProduct = (imageURL) => {
    setActiveImage(imageURL);
   }

  return (
    <div className="container mx-auto p-4">
      <div className="min-h-[200px] flex flex-col lg:flex-row gap-4">
        {/* Product Image */}
        <div className="h-96 flex flex-col lg:flex-row-reverse gap-4">
               <div className="h-[300px] w-[300px] lg:h-96 lg:w-96 bg-slate-200">
                    <img src={activeImage} alt="" className="h-full w-full object-scale-down mix-blend-multiply"/>
               </div>
          <div className="h-full">
            {loading ? (
              <div className="flex gap-2 lg:flex-col overflow-scroll scrollbar-none h-full"> 
                {productImageListLoading.map((el) => {
                  return <div className="h-20 w-20 bg-slate-200 rounded animate-pulse" key={"loadingImage"}>

                  </div>;
                })}
              </div>
            ) : (
                <div className="flex gap-2 lg:flex-col overflow-scroll scrollbar-none h-full"> 
                {data?.productImage?.map((imgURL,index) => {
                  return <div className="h-20 w-20 bg-slate-200 rounded p-1" key={imgURL}>
                          <img src={imgURL} alt="" className="h-full w-full object-scale-down mix-blend-multiply cursor-pointer" onMouseEnter={()=>handleMouseEnterProduct(imgURL)} onClick={()=>handleMouseEnterProduct(imgURL)}/>
                  </div>;
                })}
              </div>
            )}
          </div>
        </div>
        {/* Product Details */}
        <div className="flex flex-col gap-1">
              <p className="bg-red-200 text-red-600 px-2 rounded-full inline-block w-fit">{data?.brandName}</p>
              <h2 className="text-2xl lg:text-4xl font-medium">{data?.productName}</h2>
              <p className="text-slate-400 capitalize">{data?.category}</p>

              <div className="text-red-600 flex items-center gap-1">
               <FaStar/>
               <FaStar/>
               <FaStar/>
               <FaStar/>
               <FaStarHalf/>
              </div>


              <div className="flex items-center gap-2 text-2xl font-medium lg:text-3xl my-1">
                 <p className="text-red-600">{displayPKRCurrency(data?.sellingPrice)}</p>
                 <p className="text-slate-400 line-through">{displayPKRCurrency(data?.price)}</p>
              </div>
              <div className="flex items-center gap-3 my-2">
                <button className="border-2 border-red-600 rounded px-3 py-1 min-w-[120px] text-red-600 font-medium hover:bg-red-600 hover:text-white">Buy</button>
                <button className="border-2 border-red-600 rounded px-3 py-1 min-w-[120px] font-medium bg-red-600 text-white hover:bg-white hover:text-red-600">Add To Cart</button>
              </div>
              <div>
                <p className="">Description :</p>
              </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
