import React, { useEffect, useRef, useState } from "react";
import fetchCategoryWiseProduct from "../helpers/fetchCategoryWiseProduct";
import displayPKRCurrency from "../helpers/displayCurrency";
import { FaAngleLeft, FaAngleRight } from "react-icons/fa";

const VerticalCardProduct = ({ category, heading }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const scrollElement = useRef();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const categoryProduct = await fetchCategoryWiseProduct(category);
        setData(categoryProduct?.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [category]);

  const scrollRight = () => {
    scrollElement.current.scrollLeft += 300;
  };

  const scrollLeft = () => {
    scrollElement.current.scrollLeft -= 300;
  };

  const hasOverflow = () => {
    if (!scrollElement.current) return false;
    return scrollElement.current.scrollWidth > scrollElement.current.clientWidth;
  };

  return (
    <div className="container mx-auto px-4 my-6 relative">
      <h2 className="text-2xl py-4 font-semibold">{heading}</h2>
      <div className="relative">
        <button
          onClick={scrollLeft}
          className={`bg-white shadow-md rounded-full p-1 absolute left-0 top-1/2 transform -translate-y-1/2 text-lg ${!hasOverflow() ? 'hidden' : ''}`}
          aria-label="Scroll Left"
        >
          <FaAngleLeft />
        </button>
        <button
          onClick={scrollRight}
          className={`bg-white shadow-md rounded-full p-1 absolute right-0 top-1/2 transform -translate-y-1/2 text-lg ${!hasOverflow() ? 'hidden' : ''}`}
          aria-label="Scroll Right"
        >
          <FaAngleRight />
        </button>
        <div
          className="flex items-center gap-4 md:gap-6 overflow-x-scroll scrollbar-none transition-all"
          ref={scrollElement}
        >
          {loading ? (
            <div className="flex gap-4">
              {new Array(5).fill(null).map((_, index) => (
                <div
                  key={index}
                  className="w-full min-w-[280px] md:min-w-[320px] max-w-[280px] md:max-w-[320px] h-64 bg-white rounded-sm shadow animate-pulse"
                >
                  <div className="bg-slate-200 h-48 p-4 min-w-[280px] md:min-w-[320px] flex justify-center items-center" />
                  <div className="p-4 grid gap-3">
                    <div className="bg-slate-200 h-5 w-full rounded" />
                    <div className="bg-slate-200 h-4 w-3/4 rounded mt-1" />
                    <div className="bg-slate-200 h-5 w-1/2 rounded mt-2" />
                  </div>
                </div>
              ))}
            </div>
          ) : (
            data.map((product) => (
              <div
                key={product.id} // Make sure each child in a list has a unique "key" prop
                className="w-full min-w-[280px] md:min-w-[320px] max-w-[280px] md:max-w-[320px] bg-white rounded-sm shadow"
              >
                <div className="bg-slate-200 h-48 p-4 min-w-[280px] md:min-w-[320px] flex justify-center items-center">
                  <img
                    src={product.productImage[0]}
                    alt={product.productName}
                    className="h-full object-cover hover:scale-110 transition-transform mix-blend-multiply"
                  />
                </div>
                <div className="p-4 grid gap-3">
                  <h2 className="font-medium text-base md:text-lg text-ellipsis line-clamp-1 text-black">
                    {product?.productName}
                  </h2>
                  <p className="capitalize text-slate-500">{product?.category}</p>
                  <div>
                    <p className="text-red-600 font-medium">
                      {displayPKRCurrency(product?.sellingPrice)}
                    </p>
                    <p className="text-slate-500 line-through">
                      {displayPKRCurrency(product?.price)}
                    </p>
                  </div>
                  <button className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-full w-full">
                    Add to Cart
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default VerticalCardProduct;
