import React from 'react';

const DisplayImage = ({
    imgUrl,
    onClose
}) => {
  return (
   <div className='fixed bottom-0 top-0 right-0 left-0'>
     <div className='flex justify-center p-4 max-h-[80vh] '>
        <img src={imgUrl} className='w-full h-full'/>
    </div>
   </div>
  )
}

export default DisplayImage;
