import React, { useContext, useState } from "react";
import loginIcon from "../assest/signin.gif";
import { Link, useNavigate } from "react-router-dom";
import SummeryApi from "../common";
import { toast } from 'react-toastify';
import Context from "../context";



const Login = () => {
  const [data,setData] = useState({
    email:"",
    password:"",
  });
  const navigate = useNavigate();
  const generalContext = useContext(Context);

  console.log(generalContext.fetchUserDetails);

  const handleOnChange = (e) => {
       const {name,value} = e.target;

       setData((preve)=>{
        return{
         ...preve,
         [name]: value,      
        }
       });
  }

const handleSubmit = async(e) => {
    e.preventDefault();
    const dataResponse = await fetch(SummeryApi.signIn.url,{
      method: SummeryApi.signIn.method,
      credentials : "include",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(data)
    });

    const dataApi = await dataResponse.json();
    if(dataApi.success){
      toast.success(dataApi.message);
      navigate("/");
    }
    if(dataApi.error){
      toast.error(dataApi.message)
    }
}

  return (
    <section id="login">
      <div className="max-auto container p-4">
        <div className="bg-white p-5 w-full max-w-md mx-auto">
          <div className="w-16 h-16   mx-auto">
            <img src={loginIcon} alt="login icon" />
          </div>
          <form className="pt-6 flex flex-col gap-2" onSubmit={handleSubmit}>
            <div className="grid">
              <label>Email : </label>
              <div className="bg-slate-100 p-2">
                <input
                  type="email"
                  placeholder="Enter your email"
                  name="email"
                  value={data.email}
                  onChange={handleOnChange}
                  className="w-full h-full outline-none bg-transparent"
                />
              </div>
            </div>
            <div>
              <label>Password : </label>
              <div className="bg-slate-100 p-2 flex">
                <input
                  type="password"
                  placeholder="Enter your Password"
                  name="password"
                  value={data.password}
                  onChange={handleOnChange}
                  className="w-full h-full outline-none bg-transparent"
                />
              </div>
              <Link to={"/forgot-password"} className="block w-fit ml-auto hover:underline hover:text-red-600">Forgot Password</Link>
            </div>
            <button className="bg-red-600 hover:bg-red-700 text-white px-6 py-2 w-full rounded-full max-w-[150px] hover:scale-110 transition-all mx-auto block mt-4">Login</button>
          </form>
          <p className="my-5">Don't have acoount ?<Link to={"/sign-up"} className="hover:text-red-700 hover:underline"> SignUp</Link></p>

        </div>
      </div>
    </section>
  );
};

export default Login;
