import React, { useEffect, useCallback } from 'react'; // Import React, useEffect, and useCallback
import { Outlet } from 'react-router-dom';
import './App.css';
import Header from './components/Header';
import Footer from './components/Footer';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useDispatch } from 'react-redux'; // Import useDispatch from react-redux
import SummeryApi from './common';
import Context from './context';
import { setUserDetails } from './store/userSlice';

function App() {
  const dispatch = useDispatch(); // Define useDispatch here

  // Wrap fetchUserDetails in useCallback to memoize it
  const fetchUserDetails = useCallback(async () => {
    try {
      const dataResponse = await fetch(SummeryApi.current_user.url, {
        method: SummeryApi.current_user.method,
        credentials: "include",
      });
      const dataApi = await dataResponse.json();
      if (dataApi.success) {
        dispatch(setUserDetails(dataApi.data));
      }
    } catch (error) {
      console.error('Error fetching user details:', error);
    }
  }, [dispatch]); 

  useEffect(() => {
    fetchUserDetails();
  }, [fetchUserDetails]); 

  return (
    <>
      <Context.Provider value={{
        fetchUserDetails
      }}>
        <ToastContainer />
        <Header />
        <main className='min-h-[calc(100vh-120px)]'>
          <Outlet />
        </main>
        <Footer />
      </Context.Provider>
    </>
  );
}

export default App;
