import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import SummeryApi from '../common';

const ProductDetails = () => {
    const [data,setData] = useState({
        productName: "",
        brandName: "",
        category: "",
        productImage: [],
        description: "",
        price: "",
        sellingPrice: ""
    });
    const params = useParams();
    console.log("product id",params)
    const [loading, setLoading] = useState(true);
    const productImageListLoading = new Array(4).fill(null)

    const fetchProductDetails = async() => {
        setLoading(true)
        const response = await fetch(SummeryApi.productDetails.url,{
            method : SummeryApi.productDetails.method,
            headers : {
                "content-type" : "application/json"
            },
            body : JSON.stringify({
                productId : params?._id
            })
        })
        const dataResponse = response.json();
        setData(dataResponse?.data);
    }
    console.log("data",data)
    useEffect(()=>{
        fetchProductDetails()
    },[])
  return (
    <div className='container mx-auto p-4'>
        <div className='min-h-[200px]'>

           {/* Product Image */}
           <div>
               <div className='h-96'>
                {
                    loading ? (
                        <div className='flex gap-2 lg:flex-col overflow-scroll scrollbar-none'>
                            {
                                productImageListLoading.map(el =>{
                                    return (
                                        <div className='h-20 w-20 bg-slate-200 rounded'>
        
                                        </div>
                                    )
                                })
                            }
                        </div>
                     
                    ) : (
                        <div>

                        </div>
                    )
                }
               </div>
           </div>

             {/* Product Details*/}
             <div>
            
            </div>

        </div>
    </div>
  )
}

export default ProductDetails;
