import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import SummaryApi from '../common';
import { toast } from 'react-toastify';


const ForgotPassword = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [data, setData] = useState({
        email: "",
        password: ""
    });
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
  

    const handleOnChange = (e) => {
        const { name, value } = e.target;

        setData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const dataResponse = await fetch(SummaryApi.forgotPassword.url, {
                method: SummaryApi.forgotPassword.method,
                credentials: 'include',
                headers: {
                    "content-type": "application/json"
                },
                body: JSON.stringify(data)
            });

            const dataApi = await dataResponse.json();

            if (dataApi.success) {
                toast.success(dataApi.message);
                navigate('/');
            } else {
                toast.error(dataApi.message);
            }
        } catch (error) {
            toast.error("An error occurred while logging in. Please try again.");
        } finally {
            setLoading(false);
        }
    };


    return (
        <section id='forgot-password'>
            <div className='mx-auto container p-4'>
                <div className='bg-white p-5 w-full max-w-sm mx-auto'>
                    <div className='w-20 h-20 mx-auto'>
                        <h2>Forgot Password</h2>
                    </div>

                    <form className='pt-6 flex flex-col gap-2' onSubmit={handleSubmit}>
                        <div className='grid'>
                            <label htmlFor='email'>Email:</label>
                            <div className='bg-slate-100 p-2'>
                                <input
                                    type='email'
                                    id='email'
                                    placeholder='Enter email'
                                    name='email'
                                    value={data.email}
                                    onChange={handleOnChange}
                                    className='w-full h-full outline-none bg-transparent'
                                    required
                                />
                            </div>
                        </div>

                        <div>
                            <label htmlFor='password'>Password:</label>
                            <div className='bg-slate-100 p-2 flex'>
                                <input
                                    type={showPassword ? "text" : "password"}
                                    id='password'
                                    placeholder='Enter password'
                                    value={data.password}
                                    name='password'
                                    onChange={handleOnChange}
                                    className='w-full h-full outline-none bg-transparent'
                                    required
                                />
                               
                            </div>
                            
                        </div>
                        <div>
                            <label htmlFor='password'>What is Favourite Game:</label>
                            <div className='bg-slate-100 p-2 flex'>
                                <input
                                    type={showPassword ? "text" : "password"}
                                    id='password'
                                    placeholder='Enter password'
                                    value={data.password}
                                    name='password'
                                    onChange={handleOnChange}
                                    className='w-full h-full outline-none bg-transparent'
                                    required
                                />
                               
                            </div>
                            
                        </div>

                        <button
                            type='submit'
                            className='bg-red-600 hover:bg-red-700 text-white px-8 py-2 w-full max-w-[150px] rounded-full hover:scale-110 transition-all mx-auto flex mt-6'
                            disabled={loading}
                        >
                            {loading ? 'Logging in...' :'Reset Password'}
                        </button>
                    </form>
                </div>
            </div>
        </section>
    );
};

export default ForgotPassword;
