import React from "react";
import Logo from "./Logo";
import { BsSearch } from "react-icons/bs";
import { FaRegUserCircle } from "react-icons/fa";
import { FaShoppingCart } from "react-icons/fa";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const Header = () => {
  const user = useSelector(state => state?.user?.user)
  return (
    <header className="h-16 shadow-md bg-white">
      <div className="h-full container mx-auto flex items-center px-4 justify-between">
        <div className="">
        <Link to={"/"}>
        <Logo />
        </Link> 
        </div> 
        <div className="hidden lg:flex items-center w-full justify-between max-w-sm border rounded-full focus-within:shadow pl-2">
          <input
            type="text"
            placeholder="search product here...."
            className="w-full outline-none "
          />
          <div className="text-lg min-w-[50px] flex items-center justify-center h-8 bg-red-600 rounded-r-full cursor-pointer">
            <BsSearch />
          </div>
        </div>

        <div className="flex items-center gap-7">
          <div className="text-2xl cursor-pointer">
            {
              user?.profilePic (
                <img src={user?.profilePic}/>
              ) 
            }
            <FaRegUserCircle />
          </div>
          <div className="text-2xl relative">
            <span>
              <FaShoppingCart />
            </span>
            <div className="bg-red-600 text-white w-5 p-1 flex items-center justify-center h-5 rounded-full absolute -top-2 -right-3">
              <p className="text-sm">0</p>
            </div>
          </div>
          <div>
            <Link to={"/login"}  className="px-3 py-1 rounded-full text-white bg-red-600 hover:bg-red-700">Login</Link>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
