import React, { useState } from 'react';
import image1 from "../assest/banner/img1.webp";
import image2 from "../assest/banner/img2.webp";
import image3 from "../assest/banner/img3.jpg";
import image4 from "../assest/banner/img4.jpg";
import image5 from "../assest/banner/img5.webp";
import { TfiAngleRight } from "react-icons/tfi";
import { TfiAngleLeft } from "react-icons/tfi";



import image1Mobile from "../assest/banner/img1_mobile.jpg";
import image2Mobile from "../assest/banner/img2_mobile.webp";
import image3Mobile from "../assest/banner/img3_mobile.jpg";
import image4Mobile from "../assest/banner/img4_mobile.jpg";
import image5Mobile from "../assest/banner/img5_mobile.png";

const BannerProduct = () => {
    const [currentImage,setCurrentImage] = useState(0);
    const desktopImages = [
        image1,
        image2,
        image3,
        image4,
        image5
    ]
    const mobileImages = [
        image1Mobile,
        image2Mobile,
        image3Mobile,
        image4Mobile,
        image5Mobile
    ]

const nextImage = () => {
    if(desktopImages.length - 0 > currentImage){
        setCurrentImage(preve => preve + 1)
    }
}
const preveImage = () => {
    if(currentImage != 0){
        setCurrentImage(preve => preve - 1)
    }
}

  return (
    <div className='container mx-auto px-4 rounded'>
        <div className='h-60 md:h-72 w-full bg-slate-200 relative'>
            <div className='absolute z-10 w-full h-full md:flex items-center hidden'>
           <div className='flex justify-between w-full text-2xl'>
           <button onClick={preveImage} className='bg-white shadow-md rounded-full p-1'>
                <TfiAngleLeft/>
                </button>
                <button onClick={nextImage} className='bg-white shadow-md rounded-full p-1'>
                  <TfiAngleRight/>
                </button>
           </div>
            </div>
            {/* Desktop and Teblet Version */}
           <div className='hidden md:flex w-full h-full overflow-hidden'>
           {
                desktopImages.map((imageURL,index)=>{
                    return(
                        <div className='w-full h-full min-w-full min-h-full translate' key={imageURL} style={{transform : `translate(-${currentImage * 100}400%)`}}>
                        <img src={imageURL} alt='' className='w-full h-full object-cover'/>
                        </div>
                    )
                })
            }
           </div>
            {/* Mobiles Version */}
           <div className='flex w-full h-full overflow-hidden md:hidden'>
           {
                mobileImages.map((imageURL,index)=>{
                    return(
                        <div className='w-full h-full min-w-full min-h-full translate' key={imageURL} style={{transform : `translate(-${currentImage * 100}400%)`}}>
                        <img src={imageURL} alt='' className='w-full h-full'/>
                        </div>
                    )
                })
            }
           </div>
           
        </div>
    </div>
  )
}

export default BannerProduct;
