import React, { useEffect, useState } from 'react';
import SummeryApi from '../common';

const CategoryList = () => {
    const [categoryProduct,setCategoryProduct] = useState([]);
    const [loading,setLoading] = useState(false);

const fetchCategoryProduct = async() => {
    setLoading(true);
    const response = await fetch(SummeryApi.categoryProduct.url)
    const dataResponse = await response.json();
    setLoading(false);
    setCategoryProduct(dataResponse.data);
}

useEffect(()=>{
    fetchCategoryProduct();
},[]);

  return (
    <div className='container ms-auto p-4'>
       <div className='flex items-center gap-4 justify-between'>
       {
            categoryProduct.map((product,index)=>{
                return(
                    <div>
                        <div className='w-20 h-20 rounded-full overflow-hidden p-2 bg-white flex items-center justify-center'>
                            <img src={product?.productImage[0]}  alt={product?.category} className='h-full object-fill'/>
                        </div>
                    </div>
                )
            })
        }
       </div>
    </div>
  )
}

export default CategoryList;
